var laq_time = document.getElementById("laq_time"), laq_time_time = null;

function setTime() {
    var now = new Date();
    var currentYear = now.getFullYear();
    var nextAnniversary = new Date(currentYear, 9, 8); // October 8th

    if (now > nextAnniversary) {
        nextAnniversary.setFullYear(currentYear + 1);
    }

    var diff = nextAnniversary - now;
    var days = Math.floor(diff / (1000 * 60 * 60 * 24));
    var hours = Math.floor((diff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    var minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
    var seconds = Math.floor((diff % (1000 * 60)) / 1000);

    // Calculate years together
    var startDate = new Date(2014, 9, 8);
    var yearsTogether = now.getFullYear() - startDate.getFullYear();
    var monthsTogether = now.getMonth() - startDate.getMonth();
    var daysTogether = now.getDate() - startDate.getDate();

    if (daysTogether < 0) {
        monthsTogether--;
        daysTogether += new Date(now.getFullYear(), now.getMonth(), 0).getDate();
    }
    if (monthsTogether < 0) {
        yearsTogether--;
        monthsTogether += 12;
    }

    // Calculate which anniversary is next
    var nextAnniversaryYear = nextAnniversary.getFullYear() - startDate.getFullYear();

    var html = `
        <div class="love-widget">
            <div class="heart-decor heart-left">❤</div>
            <div class="heart-decor heart-right">❤</div>
            <div class="together-time">
                <span class="together-years">${yearsTogether}</span>
                <span class="together-detail">Years ${monthsTogether} Months ${daysTogether} Days</span>
            </div>
            <div class="countdown-container">
                <div class="countdown-item">
                    <div class="countdown-box">
                        <span class="countdown-value">${String(days).padStart(2, '0')}</span>
                        <span class="countdown-label">Days</span>
                    </div>
                </div>
                <div class="countdown-item">
                    <div class="countdown-box">
                        <span class="countdown-value">${String(hours).padStart(2, '0')}</span>
                        <span class="countdown-label">Hrs</span>
                    </div>
                </div>
                <div class="countdown-item">
                    <div class="countdown-box">
                        <span class="countdown-value">${String(minutes).padStart(2, '0')}</span>
                        <span class="countdown-label">Mins</span>
                    </div>
                </div>
                <div class="countdown-item">
                    <div class="countdown-box">
                        <span class="countdown-value">${String(seconds).padStart(2, '0')}</span>
                        <span class="countdown-label">Secs</span>
                    </div>
                </div>
            </div>
            <div class="anniversary-text">
             <span class="heart-beat">❤</span> Until Our ${nextAnniversaryYear}th Anniversary <span class="heart-beat">❤</span>
            </div>
        </div>
    `;

    laq_time.innerHTML = html;
}

// Add CSS styles
var style = document.createElement('style');
style.textContent = `
    .love-widget {
        display: flex;
        flex-direction: column;
        align-items: center;
        gap: 8px;
        padding: 8px;
        background: linear-gradient(135deg, rgba(255,255,255,0.1), rgba(255,255,255,0.05));
        border-radius: 10px;
        backdrop-filter: blur(10px);
        -webkit-backdrop-filter: blur(10px);
        box-shadow: 0 4px 16px rgba(255, 107, 107, 0.1);
        position: relative;
        overflow: hidden;
    }

    .heart-decor {
        position: absolute;
        color: rgba(255, 107, 107, 0.1);
        font-size: 3em;
        pointer-events: none;
        animation: float 3s ease-in-out infinite;
    }

    .heart-left {
        left: -5px;
        top: 50%;
        transform: translateY(-50%);
        animation-delay: 0.5s;
    }

    .heart-right {
        right: -5px;
        top: 50%;
        transform: translateY(-50%);
        animation-delay: 1.5s;
    }

    @keyframes float {
        0%, 100% { transform: translateY(-50%) scale(1); }
        50% { transform: translateY(-50%) scale(1.1); }
    }

    .heart-mini {
        color: #ff6b6b;
        font-size: 0.8em;
        margin: 0 2px;
        animation: pulse 1.5s ease-in-out infinite;
    }

    @keyframes pulse {
        0%, 100% { transform: scale(1); }
        50% { transform: scale(1.2); }
    }

    .heart-beat {
        display: inline-block;
        color: #ff6b6b;
        animation: heartBeat 1.3s ease-in-out infinite;
        margin-left: 2px;
    }

    @keyframes heartBeat {
        0%, 100% { transform: scale(1); }
        10%, 30% { transform: scale(1.2); }
        20%, 40% { transform: scale(0.9); }
        50%, 100% { transform: scale(1); }
    }

    .together-time {
        display: flex;
        align-items: baseline;
        gap: 5px;
    }

    .together-years {
        font-size: 1.5em;
        font-weight: bold;
        background: linear-gradient(45deg, #ff6b6b, #ff8e8e);
        -webkit-background-clip: text;
        background-clip: text;
        color: transparent;
    }

    .together-detail {
        font-size: 0.8em;
        color: #ff9999;
    }

    .countdown-container {
        display: grid;
        grid-template-columns: repeat(4, 1fr);
        gap: 4px;
        padding: 4px;
        background: rgba(255, 107, 107, 0.05);
        border-radius: 8px;
    }

    .countdown-box {
        background: linear-gradient(135deg, #ff6b6b, #ff8e8e);
        padding: 3px 6px;
        border-radius: 6px;
        box-shadow: 0 2px 8px rgba(255, 107, 107, 0.2);
        display: flex;
        flex-direction: column;
        align-items: center;
        line-height: 1;
    }

    .countdown-value {
        color: white;
        font-size: 0.9em;
        font-weight: bold;
    }

    .countdown-label {
        color: rgba(255,255,255,0.9);
        font-size: 0.6em;
        text-transform: uppercase;
    }

    .anniversary-text {
        color: #ff8e8e;
        font-size: 0.8em;
        font-weight: 500;
    }
`;
document.head.appendChild(style);

laq_time ? laq_time_time = setInterval(function () { setTime(); }, 1000) : clearInterval(laq_time_time);